import cron from "node-cron";
import dotenv from "dotenv";
import { postToSocial } from "./postToSocial.js";

dotenv.config();

console.log("📅 Social Media Scheduler Started");

/**
 * Cron Expression:
 * 0 0 */7 * *  → Runs every 7 days at 12:00 AM
 */
cron.schedule("0 0 */7 * *", async () => {
  console.log("⏰ Triggered 7-day scheduled job");
  await postToSocial();
});
